const u=(s,r=1)=>{r=Math.max(0,Math.min(r,1));const o=["rgba(255, 0, 0, alpha)","rgba(0, 255, 0, alpha)","rgba(0, 0, 255, alpha)","rgba(255, 255, 0, alpha)","rgba(255, 0, 255, alpha)","rgba(0, 255, 255, alpha)","rgba(128, 0, 0, alpha)","rgba(0, 128, 0, alpha)","rgba(0, 0, 128, alpha)","rgba(128, 128, 0, alpha)","rgba(128, 0, 128, alpha)","rgba(0, 128, 128, alpha)","rgba(192, 192, 192, alpha)","rgba(128, 128, 128, alpha)","rgba(255, 165, 0, alpha)","rgba(255, 20, 147, alpha)","rgba(75, 0, 130, alpha)","rgba(255, 105, 180, alpha)","rgba(0, 255, 127, alpha)","rgba(255, 215, 0, alpha)","rgba(255, 69, 0, alpha)"];let a;const b=o.length-1;if(a=Array.from(s).reduce((h,M)=>(h<<5)-h+M.charCodeAt(0),0),a=Math.abs(a)%b+1,a>=1&&a<=b)return{color:o[a-1].replace("alpha",r.toString()),contrastColor:`rgba(${255-Math.round(parseInt(o[a-1].split(",")[0].split("(")[1])*(r/255))}, ${255-Math.round(parseInt(o[a-1].split(",")[1]))}, ${255-Math.round(parseInt(o[a-1].split(",")[2]))}, ${r})`};const l=(a*30+20)%256,n=(a*60+100)%256,g=(a*90+150)%256,e=Math.max(l,n,g),c=Math.min(l,n,g);if(e-c===0)return{color:`rgba(255, 255, 255, ${r})`,contrastColor:`rgba(0, 0, 0, ${r})`};const t=255/e,p=`rgba(${Math.round(l*t)}, ${Math.round(n*t)}, ${Math.round(g*t)}, ${r})`,d=`rgba(${255-Math.round(l*t)}, ${255-Math.round(n*t)}, ${255-Math.round(g*t)}, ${r})`;return{color:p,contrastColor:d}};export{u as g};
