from typing import Union, Any

from pytoniq_core import Address, Cell, begin_cell

from .content import DNSCollectionContent
from .data import DNSCollectionData
from .op_codes import *
from ...contract import Contract
from ...nft.royalty_params import RoyaltyParams


class DNSCollection(Contract):
    # https://github.com/nessshon/tondns-subdomain-contracts

    CODE_HEX = ""  # noqa
    ITEM_CODE_HEX = ""  # noqa

    def __init__(
            self,
            owner_address: Union[Address, str],
            content: DNSCollectionContent,
            royalty_params: RoyaltyParams,
            domain: str,
    ) -> None:
        self._data = self.create_data(
            owner_address=owner_address,
            content=content,
            royalty_params=royalty_params,
            domain=domain,
        ).serialize()
        self._code = Cell.one_from_boc(self.CODE_HEX)

    @classmethod
    def create_data(
            cls,
            owner_address: Union[Address, str],
            content: DNSCollectionContent,
            royalty_params: RoyaltyParams,
            domain: str,
    ) -> Any:
        return DNSCollectionData(
            owner_address=owner_address,
            content=content,
            item_code=Cell.one_from_boc(cls.ITEM_CODE_HEX),
            royalty_params=royalty_params,
            domain=domain,
        )

    @classmethod
    def build_deploy_body(cls, query_id: int = 0) -> Cell:
        return (
            begin_cell()
            .store_uint(DEPLOY_OPCODE, 32)
            .store_uint(query_id, 64)
            .end_cell()
        )

    @classmethod
    def build_change_owner_body(cls, owner_address: Address, query_id: int = 0) -> Cell:
        return (
            begin_cell()
            .store_uint(CHANGE_OWNER_OPCODE, 32)
            .store_uint(query_id, 64)
            .store_address(owner_address)
            .end_cell()
        )

    @classmethod
    def build_edit_content(
            cls,
            content: DNSCollectionContent,
            royalty_params: RoyaltyParams,
            query_id: int = 0,
    ) -> Cell:
        return (
            begin_cell()
            .store_uint(EDIT_CONTENT_OPCODE, 32)
            .store_uint(query_id, 64)
            .store_ref(content.serialize())
            .store_ref(royalty_params.serialize())
            .end_cell()
        )

    @classmethod
    def build_subdomain_body(cls, subdomain: str) -> Cell:
        return (
            begin_cell()
            .store_uint(0, 32)
            .store_snake_string(subdomain)
            .end_cell()
        )
